import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "./productlist.css";

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const [error, setError] = useState(null); // For handling any issues

  const navigate = useNavigate();

  const handleAddProduct = () => {
    navigate("/addproduct");
  };

  // Fetch products from the PHP backend
  useEffect(() => {
    fetch("http://localhost/scandiweb-php/products.php") // Ensure the URL is correct
      .then((response) => {
        if (!response.ok) {
          throw new Error("Failed to fetch products");
        }
        return response.json();
      })
      .then((data) => {
        setProducts(data); // Set products data from the API response
      })
      .catch((error) => {
        setError(error.message);
      });
  }, []);

  if (error) {
    return <div className="error-message">Error: {error}</div>; // Show an error message if something goes wrong
  }

  return (
    <div>
      <h1>Product List</h1>
      <button className="add-button" onClick={handleAddProduct}>
        ADD
      </button>
      <button id="delete-product-btn">MASS DELETE</button>
      <div className="line"></div>
      <div className="product-container">
        {products.map((product) => (
          <div key={product.id} className="product-card">
            <input type="checkbox" className="delete-checkbox" />
            <p>{product.sku}</p>
            <p>{product.name}</p>
            <p>
              {product.size ? `Size: ${product.size}` : ""}
              {product.type ? ` ${product.type}` : ""}
            </p>
            <p>Price: ${product.price}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductList;
