import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "./productlist.css";

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const [selectedProducts, setSelectedProducts] = useState([]);
  const navigate = useNavigate();

  // Navigate to the add product page
  const handleAddProduct = () => {
    navigate("/addproduct");
  };

  // Fetch product data from the backend
  useEffect(() => {
    fetch("http://localhost:8000/products.php") // Adjust the endpoint URL if necessary
      .then((response) => response.json())
      .then((data) => {
        setProducts(data); // Set the fetched products
      })
      .catch((error) => console.error("Error fetching products:", error));
  }, []);

  // Handle product selection for mass delete
  const handleProductSelection = (id) => {
    if (selectedProducts.includes(id)) {
      setSelectedProducts(
        selectedProducts.filter((productId) => productId !== id)
      );
    } else {
      setSelectedProducts([...selectedProducts, id]);
    }
  };

  // Mass delete selected products
  const handleMassDelete = () => {
    if (selectedProducts.length > 0) {
      fetch("http://localhost:8000/products.php", {
        method: "DELETE",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ ids: selectedProducts }),
      })
        .then((response) => response.json())
        .then(() => {
          // After successful deletion, refetch the product list
          setProducts(
            products.filter((product) => !selectedProducts.includes(product.id))
          );
          setSelectedProducts([]); // Clear the selection
        })
        .catch((error) => console.error("Error deleting products:", error));
    }
  };

  return (
    <div>
      <h1>Product List</h1>
      <button className="add-button" onClick={handleAddProduct}>
        ADD
      </button>
      <button id="delete-product-btn" onClick={handleMassDelete}>
        MASS DELETE
      </button>
      <div className="line"></div>
      <div className="product-container">
        {products.map((product) => (
          <div key={product.id} className="product-card">
            <input
              type="checkbox"
              className="delete-checkbox"
              onChange={() => handleProductSelection(product.id)}
            />
            <h2>{product.name}</h2>
            <p>SKU: {product.SKU}</p>
            <p>Size: {product.Size}</p>
            <p>Price: ${product.price}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductList;
