import React, { useState } from "react";
import DVD from "./DVD";
import Book from "./Book";
import Furniture from "./Furniture";
import "./addproduct.css";
import { useNavigate } from "react-router-dom";

const Addproduct = () => {
  const [productType, setProductType] = useState(""); // state to manage selected product type
  const [sku, setSku] = useState("");
  const [name, setName] = useState("");
  const [price, setPrice] = useState("");
  const [size, setSize] = useState(""); // specific for DVD
  const [weight, setWeight] = useState(""); // specific for Book
  const [height, setHeight] = useState(""); // specific for Furniture
  const [width, setWidth] = useState(""); // specific for Furniture
  const [length, setLength] = useState(""); // specific for Furniture

  const navigate = useNavigate();

  const productComponents = {
    DVD: DVD,
    Book: Book,
    Furniture: Furniture,
  };

  const handleProductTypeChange = (e) => {
    setProductType(e.target.value); // update product type state on selection
  };

  const handlecancel = () => {
    navigate("/");
  };

  const handleSubmit = (event) => {
    event.preventDefault(); // Prevent the default form submission

    // Create product data object
    const productData = {
      sku,
      name,
      price,
      type: productType,
      size: productType === "DVD" ? size : undefined,
      weight: productType === "Book" ? weight : undefined,
      height: productType === "Furniture" ? height : undefined,
      width: productType === "Furniture" ? width : undefined,
      length: productType === "Furniture" ? length : undefined,
    };

    // Send product data to the backend
    fetch("http://localhost/scandiweb-php/add_product.php", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(productData),
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.error) {
          console.error("Error:", data.error);
        } else {
          console.log("Success:", data.message);
          // Optionally redirect to the product list or display success message
          navigate("/");
        }
      })
      .catch((error) => console.error("Error:", error));
  };

  const ProductSpecificForm = productComponents[productType];

  return (
    <div>
      <h1>Add Product</h1>
      <button id="cancel-product-btn" onClick={handlecancel}>
        CANCEL
      </button>
      <div className="line"></div>
      <div className="form-container">
        <form id="product_form" onSubmit={handleSubmit}>
          <div>
            <label htmlFor="sku">SKU:</label>
            <input
              type="text"
              id="sku"
              name="sku"
              value={sku}
              onChange={(e) => setSku(e.target.value)}
              required
            />
          </div>

          <div>
            <label htmlFor="name">Name:</label>
            <input
              type="text"
              id="name"
              name="name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
            />
          </div>

          <div>
            <label htmlFor="price">Price ($):</label>
            <input
              type="number"
              id="price"
              name="price"
              value={price}
              onChange={(e) => setPrice(e.target.value)}
              required
            />
          </div>

          <div>
            <label htmlFor="productType">Product Type:</label>
            <select
              id="productType"
              value={productType}
              onChange={handleProductTypeChange}
              required
            >
              <option value="">Select a type</option>
              <option value="DVD">DVD</option>
              <option value="Book">Book</option>
              <option value="Furniture">Furniture</option>
            </select>
          </div>

          {/* Render product-specific fields based on selected type */}
          {ProductSpecificForm && (
            <ProductSpecificForm
              setSize={setSize}
              setWeight={setWeight}
              setHeight={setHeight}
              setWidth={setWidth}
              setLength={setLength}
            />
          )}

          <button type="submit" className="save-button">
            SAVE
          </button>
        </form>
      </div>
    </div>
  );
};

export default Addproduct;
