import React, { useState, useEffect } from "react";
import DVD from "./DVD";
import Book from "./Book";
import Furniture from "./Furniture";
import "./addproduct.css";
import { useNavigate } from "react-router-dom";

const Addproduct = () => {
  const [productType, setProductType] = useState(""); // state to manage selected product type
  const [sku, setSku] = useState("");
  const [name, setName] = useState("");
  const [price, setPrice] = useState("");
  const [size, setSize] = useState(""); // for DVD
  const [weight, setWeight] = useState(""); // for Book
  const [height, setHeight] = useState(""); // for Furniture
  const [width, setWidth] = useState(""); // for Furniture
  const [length, setLength] = useState(""); // for Furniture

  const navigate = useNavigate();

  const productComponents = {
    DVD: DVD,
    Book: Book,
    Furniture: Furniture,
  };

  const handleProductTypeChange = (e) => {
    setProductType(e.target.value); // update product type state on selection
  };

  const handlecancel = () => {
    navigate("/");
  };

  const handleSubmit = (e) => {
    e.preventDefault(); // Prevent default form submission

    const productData = {
      sku,
      name,
      price,
      type: productType,
      size,
      weight,
      height,
      width,
      length,
    };

    fetch("http://localhost/scandiweb-php/add_product.php", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(productData),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error("Failed to save product");
        }
        return response.json();
      })
      .then((data) => {
        console.log("Product saved successfully", data);
        navigate("/"); // Navigate back to the product list or any other desired page
      })
      .catch((error) => {
        console.error("Error:", error);
      });
  };

  const ProductSpecificForm = productComponents[productType];

  return (
    <div>
      <h1>Product List</h1>
      <button type="submit" className="save-button" onClick={handleSubmit}>
        SAVE
      </button>
      <button id="cancel-product-btn" onClick={handlecancel}>
        CANCEL
      </button>
      <div className="line"></div>
      <div className="form-container">
        <form id="product_form" onSubmit={handleSubmit}>
          <div>
            <label htmlFor="sku">SKU:</label>
            <input
              type="text"
              id="sku"
              name="sku"
              value={sku}
              onChange={(e) => setSku(e.target.value)}
              required
            />
          </div>

          <div>
            <label htmlFor="name">Name:</label>
            <input
              type="text"
              id="name"
              name="name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
            />
          </div>

          <div>
            <label htmlFor="price">Price ($):</label>
            <input
              type="number"
              id="price"
              name="price"
              value={price}
              onChange={(e) => setPrice(e.target.value)}
              required
            />
          </div>

          <div>
            <label htmlFor="productType">Product Type:</label>
            <select
              id="productType"
              value={productType}
              onChange={handleProductTypeChange}
              required
            >
              <option value="">Select a type</option>
              <option id="DVD" value="DVD">
                DVD
              </option>
              <option id="Book" value="Book">
                Book
              </option>
              <option id="Furniture" value="Furniture">
                Furniture
              </option>
            </select>
          </div>

          {/* Render product-specific fields based on selected type */}
          {ProductSpecificForm && <ProductSpecificForm />}
        </form>
      </div>
    </div>
  );
};

export default Addproduct;
